import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import QrScanner from 'react-qr-scanner';
import { CameraIcon, ArrowLeftIcon, CheckCircleIcon, XCircleIcon } from '@heroicons/react/24/outline';
import { equipmentService } from '../../services/equipmentService';
import toast from 'react-hot-toast';

const QRScanner = () => {
  const navigate = useNavigate();
  const [scanning, setScanning] = useState(true);
  const [scannedData, setScannedData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const scannerRef = useRef(null);

  const handleScan = async (data) => {
    if (data && !scannedData) {
      setScanning(false);
      setScannedData(data.text);

      try {
        setLoading(true);
        setError(null);

        // Versuche Equipment Ã¼ber QR-Code zu finden
        const equipment = await equipmentService.getEquipmentByQR(data.text);

        if (equipment) {
          toast.success('Equipment gefunden!');
          // Navigiere zur Equipment-Detailseite
          navigate(`/equipment/${equipment._id}`);
        } else {
          setError('Equipment nicht gefunden');
          toast.error('Equipment nicht gefunden');
        }
      } catch (err) {
        console.error('Error scanning QR code:', err);
        setError('Fehler beim Scannen des QR-Codes');
        toast.error('Fehler beim Scannen des QR-Codes');
      } finally {
        setLoading(false);
      }
    }
  };

  const handleError = (err) => {
    console.error('QR Scanner error:', err);
    setError('Kamera-Fehler: ' + err.message);
  };

  const resetScanner = () => {
    setScanning(true);
    setScannedData(null);
    setError(null);
    setLoading(false);
  };

  const handleManualInput = () => {
    const qrCode = prompt('QR-Code manuell eingeben:');
    if (qrCode) {
      setScannedData(qrCode);
      handleScan({ text: qrCode });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-md mx-auto p-4">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <button
            onClick={() => navigate(-1)}
            className="flex items-center text-gray-600 hover:text-gray-900"
          >
            <ArrowLeftIcon className="h-5 w-5 mr-2" />
            ZurÃ¼ck
          </button>
          <h1 className="text-xl font-bold text-gray-900">QR-Code Scanner</h1>
          <div className="w-10"></div> {/* Spacer */}
        </div>

        {/* Scanner Container */}
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          {scanning ? (
            <div className="relative">
              {/* Camera View */}
              <div className="aspect-square bg-black">
                <QrScanner
                  ref={scannerRef}
                  delay={300}
                  onError={handleError}
                  onScan={handleScan}
                  style={{ width: '100%', height: '100%' }}
                  constraints={{
                    video: {
                      facingMode: 'environment' // Use back camera
                    }
                  }}
                />
              </div>

              {/* Scanner Overlay */}
              <div className="absolute inset-0 pointer-events-none">
                <div className="flex items-center justify-center h-full">
                  <div className="border-2 border-white rounded-lg p-4">
                    <div className="w-48 h-48 border-2 border-primary-500 rounded-lg relative">
                      {/* Corner indicators */}
                      <div className="absolute top-0 left-0 w-6 h-6 border-l-4 border-t-4 border-primary-500"></div>
                      <div className="absolute top-0 right-0 w-6 h-6 border-r-4 border-t-4 border-primary-500"></div>
                      <div className="absolute bottom-0 left-0 w-6 h-6 border-l-4 border-b-4 border-primary-500"></div>
                      <div className="absolute bottom-0 right-0 w-6 h-6 border-r-4 border-b-4 border-primary-500"></div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Instructions */}
              <div className="absolute bottom-4 left-4 right-4 text-center text-white text-sm">
                <p>QR-Code in den Rahmen zentrieren</p>
              </div>
            </div>
          ) : (
            <div className="p-6">
              {loading ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
                  <p className="text-gray-600">Equipment wird gesucht...</p>
                </div>
              ) : error ? (
                <div className="text-center py-8">
                  <XCircleIcon className="h-16 w-16 text-red-500 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">Fehler</h3>
                  <p className="text-gray-600 mb-4">{error}</p>
                  <button
                    onClick={resetScanner}
                    className="btn btn-primary"
                  >
                    Erneut versuchen
                  </button>
                </div>
              ) : (
                <div className="text-center py-8">
                  <CheckCircleIcon className="h-16 w-16 text-green-500 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">QR-Code erkannt</h3>
                  <p className="text-gray-600 mb-4">Weiterleitung zu Equipment...</p>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="mt-6 space-y-3">
          {scanning && (
            <>
              <button
                onClick={handleManualInput}
                className="w-full btn btn-secondary"
              >
                QR-Code manuell eingeben
              </button>

              <button
                onClick={() => navigate('/equipment')}
                className="w-full btn btn-outline"
              >
                Zur Equipment-Liste
              </button>
            </>
          )}

          {!scanning && (
            <button
              onClick={resetScanner}
              className="w-full btn btn-primary"
            >
              Neuen QR-Code scannen
            </button>
          )}
        </div>

        {/* Camera Permissions Info */}
        <div className="mt-6 p-4 bg-blue-50 rounded-lg">
          <div className="flex items-start">
            <CameraIcon className="h-5 w-5 text-blue-600 mt-0.5 mr-3" />
            <div className="text-sm text-blue-800">
              <p className="font-medium">Kamera-Berechtigung erforderlich</p>
              <p className="mt-1">Die App benÃ¶tigt Zugriff auf deine Kamera, um QR-Codes zu scannen.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QRScanner;
