import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  CameraIcon, 
  CalendarIcon, 
  UserGroupIcon, 
  MapPinIcon,
  ChartBarIcon,
  ClockIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import api from '../../services/api';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalEquipment: 0,
    availableEquipment: 0,
    activeBookings: 0,
    totalUsers: 0
  });
  const [recentBookings, setRecentBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);

      // Lade Equipment-Daten
      const equipmentResponse = await api.get('/equipment');
      if (equipmentResponse.data.success) {
        const equipment = equipmentResponse.data.data;
        setStats(prev => ({
          ...prev,
          totalEquipment: equipment.length,
          availableEquipment: equipment.filter(e => e.status === 'available').length
        }));
      }

      // Lade Buchungs-Daten
      const bookingsResponse = await api.get('/bookings');
      if (bookingsResponse.data.success) {
        const bookings = bookingsResponse.data.data;
        setStats(prev => ({
          ...prev,
          activeBookings: bookings.filter(b => b.status === 'active').length
        }));
        setRecentBookings(bookings.slice(0, 5));
      }

      // Lade User-Daten (nur für Admins)
      if (user?.role === 'admin') {
        const usersResponse = await api.get('/users');
        if (usersResponse.data.success) {
          setStats(prev => ({
            ...prev,
            totalUsers: usersResponse.data.data.length
          }));
        }
      }
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const statCards = [
    {
      title: 'Gesamt Equipment',
      value: stats.totalEquipment,
      icon: CameraIcon,
      color: 'bg-blue-500',
      link: '/equipment'
    },
    {
      title: 'Verfügbar',
      value: stats.availableEquipment,
      icon: MapPinIcon,
      color: 'bg-green-500',
      link: '/equipment?status=available'
    },
    {
      title: 'Aktive Buchungen',
      value: stats.activeBookings,
      icon: CalendarIcon,
      color: 'bg-yellow-500',
      link: '/bookings?status=active'
    },
    ...(user?.role === 'admin' ? [{
      title: 'Benutzer',
      value: stats.totalUsers,
      icon: UserGroupIcon,
      color: 'bg-purple-500',
      link: '/users'
    }] : [])
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">
          Willkommen zurück, {user?.firstName}!
        </h1>
        <p className="text-gray-600">
          Hier ist eine Übersicht über Ihr Equipment-Management System
        </p>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((stat, index) => (
          <Link
            key={index}
            to={stat.link}
            className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow"
          >
            <div className="flex items-center">
              <div className={`p-3 rounded-lg ${stat.color}`}>
                <stat.icon className="h-6 w-6 text-white" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">{stat.title}</p>
                <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
              </div>
            </div>
          </Link>
        ))}
      </div>

      {/* Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Bookings */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="p-6 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-medium text-gray-900">Aktuelle Buchungen</h3>
              <Link 
                to="/bookings" 
                className="text-blue-600 hover:text-blue-700 text-sm font-medium"
              >
                Alle anzeigen
              </Link>
            </div>
          </div>
          <div className="divide-y divide-gray-200">
            {recentBookings.length > 0 ? (
              recentBookings.map((booking) => (
                <div key={booking._id} className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium text-gray-900">
                        {booking.equipment?.name}
                      </p>
                      <p className="text-sm text-gray-600">
                        {booking.user?.firstName} {booking.user?.lastName}
                      </p>
                    </div>
                    <div className="text-right">
                      <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${
                        booking.status === 'active' 
                          ? 'bg-green-100 text-green-800'
                          : booking.status === 'pending'
                          ? 'bg-yellow-100 text-yellow-800'
                          : 'bg-gray-100 text-gray-800'
                      }`}>
                        {booking.status}
                      </span>
                      <p className="text-sm text-gray-600 mt-1">
                        {booking.startDate} - {booking.endDate}
                      </p>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="p-6 text-center text-gray-500">
                <CalendarIcon className="mx-auto h-12 w-12 text-gray-400" />
                <p className="mt-2">Keine aktuellen Buchungen</p>
              </div>
            )}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="p-6 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">Schnellaktionen</h3>
          </div>
          <div className="p-6 space-y-4">
            <Link
              to="/bookings/new"
              className="flex items-center p-3 rounded-lg border border-gray-200 hover:bg-gray-50 transition-colors"
            >
              <CalendarIcon className="h-5 w-5 text-blue-600" />
              <span className="ml-3 font-medium">Neue Buchung erstellen</span>
            </Link>

            <Link
              to="/equipment"
              className="flex items-center p-3 rounded-lg border border-gray-200 hover:bg-gray-50 transition-colors"
            >
              <CameraIcon className="h-5 w-5 text-green-600" />
              <span className="ml-3 font-medium">Equipment durchsuchen</span>
            </Link>

            <Link
              to="/scanner"
              className="flex items-center p-3 rounded-lg border border-gray-200 hover:bg-gray-50 transition-colors"
            >
              <ChartBarIcon className="h-5 w-5 text-purple-600" />
              <span className="ml-3 font-medium">QR-Code scannen</span>
            </Link>

            {user?.role === 'admin' && (
              <Link
                to="/equipment/new"
                className="flex items-center p-3 rounded-lg border border-gray-200 hover:bg-gray-50 transition-colors"
              >
                <ClockIcon className="h-5 w-5 text-orange-600" />
                <span className="ml-3 font-medium">Equipment hinzufügen</span>
              </Link>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
