import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { CalendarIcon, ClockIcon, UserIcon, EllipsisVerticalIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import { bookingService } from '../../services/bookingService';
import toast from 'react-hot-toast';

const BookingCard = ({ booking, onUpdate }) => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [showMenu, setShowMenu] = useState(false);

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending':
        return 'text-blue-600 bg-blue-100';
      case 'active':
        return 'text-green-600 bg-green-100';
      case 'completed':
        return 'text-gray-600 bg-gray-100';
      case 'cancelled':
        return 'text-red-600 bg-red-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending':
        return 'Ausstehend';
      case 'active':
        return 'Aktiv';
      case 'completed':
        return 'Abgeschlossen';
      case 'cancelled':
        return 'Storniert';
      default:
        return status;
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('de-DE', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const calculateDuration = () => {
    const start = new Date(booking.startDate);
    const end = new Date(booking.endDate);
    const diffTime = Math.abs(end - start);
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  const handleStatusChange = async (newStatus) => {
    try {
      setLoading(true);
      await bookingService.updateBooking(booking._id, { status: newStatus });
      toast.success('Buchungsstatus erfolgreich aktualisiert');
      if (onUpdate) onUpdate();
    } catch (error) {
      console.error('Error updating booking status:', error);
      toast.error('Fehler beim Aktualisieren des Status');
    } finally {
      setLoading(false);
      setShowMenu(false);
    }
  };

  const handleCancel = async () => {
    if (!window.confirm('MÃ¶chten Sie diese Buchung wirklich stornieren?')) return;

    try {
      setLoading(true);
      await bookingService.cancelBooking(booking._id);
      toast.success('Buchung erfolgreich storniert');
      if (onUpdate) onUpdate();
    } catch (error) {
      console.error('Error cancelling booking:', error);
      toast.error('Fehler beim Stornieren der Buchung');
    } finally {
      setLoading(false);
      setShowMenu(false);
    }
  };

  const canManageBooking = user.role === 'admin' || user.role === 'staff' || booking.user === user._id;

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
      <div className="flex items-start justify-between">
        <div className="flex-1">
          {/* Equipment Info */}
          <div className="flex items-center mb-3">
            <div className="flex-shrink-0 w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center">
              {booking.equipment?.images?.[0] ? (
                <img
                  src={booking.equipment.images[0].url}
                  alt={booking.equipment.name}
                  className="w-full h-full object-cover rounded-lg"
                />
              ) : (
                <span className="text-gray-500 text-lg">ð·</span>
              )}
            </div>
            <div className="ml-3">
              <h3 className="text-lg font-medium text-gray-900">
                {booking.equipment?.name}
              </h3>
              <p className="text-sm text-gray-600">
                {booking.equipment?.brand} {booking.equipment?.model}
              </p>
            </div>
          </div>

          {/* Booking Details */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            <div className="flex items-center text-sm text-gray-600">
              <CalendarIcon className="h-4 w-4 mr-2" />
              <span>
                {formatDate(booking.startDate)} - {formatDate(booking.endDate)}
              </span>
            </div>
            <div className="flex items-center text-sm text-gray-600">
              <ClockIcon className="h-4 w-4 mr-2" />
              <span>{calculateDuration()} Tag{calculateDuration() !== 1 ? 'e' : ''}</span>
            </div>
            {user.role === 'admin' && (
              <div className="flex items-center text-sm text-gray-600">
                <UserIcon className="h-4 w-4 mr-2" />
                <span>
                  {booking.user?.firstName} {booking.user?.lastName}
                </span>
              </div>
            )}
          </div>

          {/* Notes */}
          {booking.notes && (
            <div className="mb-4">
              <p className="text-sm text-gray-600 bg-gray-50 p-2 rounded">
                {booking.notes}
              </p>
            </div>
          )}

          {/* Status and Actions */}
          <div className="flex items-center justify-between">
            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(booking.status)}`}>
              {getStatusText(booking.status)}
            </span>

            <div className="flex items-center space-x-2">
              <Link
                to={`/bookings/${booking._id}`}
                className="text-sm text-primary-600 hover:text-primary-700 font-medium"
              >
                Details anzeigen
              </Link>

              {canManageBooking && (
                <div className="relative">
                  <button
                    onClick={() => setShowMenu(!showMenu)}
                    className="p-1 text-gray-400 hover:text-gray-600"
                    disabled={loading}
                  >
                    <EllipsisVerticalIcon className="h-4 w-4" />
                  </button>

                  {showMenu && (
                    <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg border border-gray-200 z-10">
                      <div className="py-1">
                        {booking.status === 'pending' && (
                          <>
                            <button
                              onClick={() => handleStatusChange('active')}
                              className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                              disabled={loading}
                            >
                              Als aktiv markieren
                            </button>
                            <button
                              onClick={handleCancel}
                              className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-100"
                              disabled={loading}
                            >
                              Stornieren
                            </button>
                          </>
                        )}

                        {booking.status === 'active' && (
                          <button
                            onClick={() => handleStatusChange('completed')}
                            className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                            disabled={loading}
                          >
                            Als abgeschlossen markieren
                          </button>
                        )}

                        <Link
                          to={`/bookings/${booking._id}/edit`}
                          className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                        >
                          Bearbeiten
                        </Link>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Loading overlay */}
      {loading && (
        <div className="absolute inset-0 bg-white bg-opacity-75 flex items-center justify-center rounded-lg">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary-600"></div>
        </div>
      )}
    </div>
  );
};

export default BookingCard;
