import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  CameraIcon,
  CalendarIcon,
  UserIcon,
  MapPinIcon,
  PlusIcon,
  QrCodeIcon,
  ChartBarIcon,
} from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import { equipmentAPI } from '../../services/api';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const { user, isAdmin, isStaff } = useAuth();
  const [stats, setStats] = useState(null);
  const [recentEquipment, setRecentEquipment] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const [statsResponse, equipmentResponse] = await Promise.all([
          equipmentAPI.getStats(),
          equipmentAPI.getAll({ limit: 5, sortBy: 'createdAt', sortOrder: 'desc' })
        ]);

        setStats(statsResponse.data.overview);
        setRecentEquipment(equipmentResponse.data.equipment);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Fehler beim Laden der Dashboard-Daten');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const quickActions = [
    {
      name: 'Equipment hinzufügen',
      href: '/equipment/new',
      icon: PlusIcon,
      color: 'bg-primary-500',
      description: 'Neues Equipment zur Datenbank hinzufügen',
      show: isStaff,
    },
    {
      name: 'Buchung erstellen',
      href: '/bookings/new',
      icon: CalendarIcon,
      color: 'bg-success-500',
      description: 'Neue Buchung für Equipment erstellen',
      show: true,
    },
    {
      name: 'QR Scanner',
      href: '/scanner',
      icon: QrCodeIcon,
      color: 'bg-warning-500',
      description: 'QR-Codes scannen und Equipment verwalten',
      show: isStaff,
    },
    {
      name: 'Statistiken',
      href: '/statistics',
      icon: ChartBarIcon,
      color: 'bg-secondary-500',
      description: 'Detaillierte Statistiken und Berichte',
      show: isAdmin,
    },
  ];

  const StatCard = ({ title, value, icon: Icon, color, change }) => (
    <div className="card">
      <div className="card-body">
        <div className="flex items-center">
          <div className={`p-3 rounded-lg ${color}`}>
            <Icon className="h-6 w-6 text-white" />
          </div>
          <div className="ml-4">
            <p className="text-sm font-medium text-gray-600">{title}</p>
            <p className="text-2xl font-semibold text-gray-900">{value}</p>
            {change && (
              <p className={`text-sm ${change > 0 ? 'text-success-600' : 'text-danger-600'}`}>
                {change > 0 ? '+' : ''}{change}% vs. letzter Monat
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Header */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h1 className="text-2xl font-bold text-gray-900">
          Willkommen zurück, {user?.firstName}!
        </h1>
        <p className="text-gray-600 mt-1">
          Hier ist eine Übersicht über Ihr Filmequipment-Management-System.
        </p>
      </div>

      {/* Statistics */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatCard
            title="Gesamt Equipment"
            value={stats.total || 0}
            icon={CameraIcon}
            color="bg-primary-500"
          />
          <StatCard
            title="Verfügbar"
            value={stats.available || 0}
            icon={CameraIcon}
            color="bg-success-500"
          />
          <StatCard
            title="Verliehen"
            value={stats.rented || 0}
            icon={CalendarIcon}
            color="bg-warning-500"
          />
          <StatCard
            title="In Wartung"
            value={stats.maintenance || 0}
            icon={ChartBarIcon}
            color="bg-secondary-500"
          />
        </div>
      )}

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {quickActions
          .filter(action => action.show)
          .map((action) => (
            <Link
              key={action.name}
              to={action.href}
              className="card hover:shadow-medium transition-shadow duration-200 group"
            >
              <div className="card-body text-center">
                <div className={`inline-flex p-3 rounded-lg ${action.color} mb-4`}>
                  <action.icon className="h-6 w-6 text-white" />
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2 group-hover:text-primary-600 transition-colors">
                  {action.name}
                </h3>
                <p className="text-sm text-gray-600">{action.description}</p>
              </div>
            </Link>
          ))}
      </div>

      {/* Recent Equipment */}
      <div className="card">
        <div className="card-header">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">Kürzlich hinzugefügtes Equipment</h2>
            <Link
              to="/equipment"
              className="text-sm text-primary-600 hover:text-primary-500 font-medium"
            >
              Alle anzeigen
            </Link>
          </div>
        </div>
        <div className="card-body">
          {recentEquipment.length > 0 ? (
            <div className="space-y-4">
              {recentEquipment.map((equipment) => (
                <div
                  key={equipment._id}
                  className="flex items-center justify-between p-4 bg-gray-50 rounded-lg"
                >
                  <div className="flex items-center space-x-4">
                    <div className="h-10 w-10 bg-primary-100 rounded-lg flex items-center justify-center">
                      <CameraIcon className="h-5 w-5 text-primary-600" />
                    </div>
                    <div>
                      <h3 className="text-sm font-medium text-gray-900">{equipment.name}</h3>
                      <p className="text-sm text-gray-500">
                        {equipment.brand} {equipment.model}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className={`status-badge ${equipment.status === 'available' ? 'status-available' : 'status-booked'}`}>
                      {equipment.status === 'available' ? 'Verfügbar' : 'Gebucht'}
                    </span>
                    <Link
                      to={`/equipment/${equipment._id}`}
                      className="text-sm text-primary-600 hover:text-primary-500"
                    >
                      Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <CameraIcon className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">Kein Equipment vorhanden</h3>
              <p className="mt-1 text-sm text-gray-500">
                Beginnen Sie mit dem Hinzufügen von Equipment.
              </p>
              {isStaff && (
                <div className="mt-6">
                  <Link
                    to="/equipment/new"
                    className="btn-primary"
                  >
                    Equipment hinzufügen
                  </Link>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* User Info */}
      <div className="card">
        <div className="card-header">
          <h2 className="text-lg font-semibold text-gray-900">Ihr Profil</h2>
        </div>
        <div className="card-body">
          <div className="flex items-center space-x-4">
            <div className="h-12 w-12 bg-primary-600 rounded-full flex items-center justify-center">
              <span className="text-sm font-medium text-white">
                {user?.firstName?.charAt(0)}{user?.lastName?.charAt(0)}
              </span>
            </div>
            <div>
              <h3 className="text-lg font-medium text-gray-900">
                {user?.firstName} {user?.lastName}
              </h3>
              <p className="text-sm text-gray-500">{user?.email}</p>
              <p className="text-sm text-gray-500 capitalize">
                Rolle: {user?.role === 'admin' ? 'Administrator' : user?.role === 'staff' ? 'Mitarbeiter' : 'Externer Nutzer'}
              </p>
            </div>
            <div className="ml-auto">
              <Link
                to="/profile"
                className="btn-outline btn-sm"
              >
                Profil bearbeiten
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
