import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { EyeIcon, EyeSlashIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import toast from 'react-hot-toast';

const Register = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { register: registerUser } = useAuth();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();

  const password = watch('password');

  const onSubmit = async (data) => {
    if (data.password !== data.confirmPassword) {
      toast.error('Passwörter stimmen nicht überein');
      return;
    }

    setIsLoading(true);
    try {
      const userData = {
        username: data.username,
        email: data.email,
        password: data.password,
        firstName: data.firstName,
        lastName: data.lastName,
        department: data.department,
        phone: data.phone,
      };

      const result = await registerUser(userData);
      if (result.success) {
        toast.success('Registrierung erfolgreich!');
        navigate('/dashboard');
      } else {
        toast.error(result.error);
      }
    } catch (error) {
      toast.error('Ein Fehler ist aufgetreten. Bitte versuchen Sie es erneut.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <div className="mx-auto h-12 w-12 flex items-center justify-center rounded-full bg-primary-100">
            <svg
              className="h-8 w-8 text-primary-600"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M18 9v3m0 0v3m0-3h3m-3 0h-3m-2-5a4 4 0 11-8 0 4 4 0 018 0zM3 20a6 6 0 0112 0v1H3v-1z"
              />
            </svg>
          </div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Konto erstellen
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Oder{' '}
            <Link
              to="/login"
              className="font-medium text-primary-600 hover:text-primary-500"
            >
              melden Sie sich mit einem bestehenden Konto an
            </Link>
          </p>
        </div>

        <form className="mt-8 space-y-6" onSubmit={handleSubmit(onSubmit)}>
          <div className="space-y-4">
            {/* Username */}
            <div>
              <label htmlFor="username" className="form-label">
                Benutzername
              </label>
              <input
                id="username"
                type="text"
                className={`form-input ${errors.username ? 'border-danger-500' : ''}`}
                placeholder="benutzername"
                {...register('username', {
                  required: 'Benutzername ist erforderlich',
                  minLength: {
                    value: 3,
                    message: 'Benutzername muss mindestens 3 Zeichen lang sein',
                  },
                  pattern: {
                    value: /^[a-zA-Z0-9_]+$/,
                    message: 'Benutzername kann nur Buchstaben, Zahlen und Unterstriche enthalten',
                  },
                })}
              />
              {errors.username && (
                <p className="form-error">{errors.username.message}</p>
              )}
            </div>

            {/* Email */}
            <div>
              <label htmlFor="email" className="form-label">
                E-Mail-Adresse
              </label>
              <input
                id="email"
                type="email"
                autoComplete="email"
                className={`form-input ${errors.email ? 'border-danger-500' : ''}`}
                placeholder="ihre@email.com"
                {...register('email', {
                  required: 'E-Mail ist erforderlich',
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: 'Ungültige E-Mail-Adresse',
                  },
                })}
              />
              {errors.email && (
                <p className="form-error">{errors.email.message}</p>
              )}
            </div>

            {/* First Name */}
            <div>
              <label htmlFor="firstName" className="form-label">
                Vorname
              </label>
              <input
                id="firstName"
                type="text"
                autoComplete="given-name"
                className={`form-input ${errors.firstName ? 'border-danger-500' : ''}`}
                placeholder="Max"
                {...register('firstName', {
                  required: 'Vorname ist erforderlich',
                  minLength: {
                    value: 1,
                    message: 'Vorname ist erforderlich',
                  },
                })}
              />
              {errors.firstName && (
                <p className="form-error">{errors.firstName.message}</p>
              )}
            </div>

            {/* Last Name */}
            <div>
              <label htmlFor="lastName" className="form-label">
                Nachname
              </label>
              <input
                id="lastName"
                type="text"
                autoComplete="family-name"
                className={`form-input ${errors.lastName ? 'border-danger-500' : ''}`}
                placeholder="Mustermann"
                {...register('lastName', {
                  required: 'Nachname ist erforderlich',
                  minLength: {
                    value: 1,
                    message: 'Nachname ist erforderlich',
                  },
                })}
              />
              {errors.lastName && (
                <p className="form-error">{errors.lastName.message}</p>
              )}
            </div>

            {/* Department */}
            <div>
              <label htmlFor="department" className="form-label">
                Abteilung (optional)
              </label>
              <input
                id="department"
                type="text"
                className="form-input"
                placeholder="Film Production"
                {...register('department')}
              />
            </div>

            {/* Phone */}
            <div>
              <label htmlFor="phone" className="form-label">
                Telefonnummer (optional)
              </label>
              <input
                id="phone"
                type="tel"
                autoComplete="tel"
                className="form-input"
                placeholder="+49 123 456789"
                {...register('phone')}
              />
            </div>

            {/* Password */}
            <div>
              <label htmlFor="password" className="form-label">
                Passwort
              </label>
              <div className="relative">
                <input
                  id="password"
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  className={`form-input pr-10 ${errors.password ? 'border-danger-500' : ''}`}
                  placeholder="••••••••"
                  {...register('password', {
                    required: 'Passwort ist erforderlich',
                    minLength: {
                      value: 6,
                      message: 'Passwort muss mindestens 6 Zeichen lang sein',
                    },
                    pattern: {
                      value: /\d/,
                      message: 'Passwort muss mindestens eine Zahl enthalten',
                    },
                  })}
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeSlashIcon className="h-5 w-5 text-gray-400" />
                  ) : (
                    <EyeIcon className="h-5 w-5 text-gray-400" />
                  )}
                </button>
              </div>
              {errors.password && (
                <p className="form-error">{errors.password.message}</p>
              )}
            </div>

            {/* Confirm Password */}
            <div>
              <label htmlFor="confirmPassword" className="form-label">
                Passwort bestätigen
              </label>
              <div className="relative">
                <input
                  id="confirmPassword"
                  type={showConfirmPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  className={`form-input pr-10 ${errors.confirmPassword ? 'border-danger-500' : ''}`}
                  placeholder="••••••••"
                  {...register('confirmPassword', {
                    required: 'Passwort-Bestätigung ist erforderlich',
                    validate: (value) =>
                      value === password || 'Passwörter stimmen nicht überein',
                  })}
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? (
                    <EyeSlashIcon className="h-5 w-5 text-gray-400" />
                  ) : (
                    <EyeIcon className="h-5 w-5 text-gray-400" />
                  )}
                </button>
              </div>
              {errors.confirmPassword && (
                <p className="form-error">{errors.confirmPassword.message}</p>
              )}
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={isLoading}
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isLoading ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
              ) : (
                'Konto erstellen'
              )}
            </button>
          </div>

          <div className="text-center">
            <p className="text-xs text-gray-500">
              Durch die Registrierung stimmen Sie unseren{' '}
              <Link to="/terms" className="text-primary-600 hover:text-primary-500">
                Nutzungsbedingungen
              </Link>{' '}
              und{' '}
              <Link to="/privacy" className="text-primary-600 hover:text-primary-500">
                Datenschutzrichtlinien
              </Link>{' '}
              zu.
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
