import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import {
  CameraIcon,
  MapPinIcon,
  TagIcon,
  CalendarIcon,
  CurrencyEuroIcon,
  WrenchScrewdriverIcon,
  QrCodeIcon,
  PencilIcon,
  TrashIcon,
} from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import { equipmentAPI } from '../../services/api';
import toast from 'react-hot-toast';

const EquipmentDetail = () => {
  const { id } = useParams();
  const { user, isStaff, isAdmin } = useAuth();
  const navigate = useNavigate();
  const [equipment, setEquipment] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchEquipment();
  }, [id]);

  const fetchEquipment = async () => {
    try {
      setLoading(true);
      const response = await equipmentAPI.getById(id);
      setEquipment(response.data.equipment);
    } catch (error) {
      console.error('Error fetching equipment:', error);
      toast.error('Fehler beim Laden der Equipment-Details');
      navigate('/equipment');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Sind Sie sicher, dass Sie dieses Equipment lÃ¶schen mÃ¶chten?')) {
      return;
    }

    try {
      await equipmentAPI.delete(id);
      toast.success('Equipment erfolgreich gelÃ¶scht');
      navigate('/equipment');
    } catch (error) {
      console.error('Error deleting equipment:', error);
      toast.error('Fehler beim LÃ¶schen des Equipment');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'available':
        return 'status-available';
      case 'booked':
        return 'status-booked';
      case 'rented':
        return 'status-rented';
      case 'maintenance':
        return 'status-maintenance';
      case 'damaged':
        return 'status-damaged';
      case 'lost':
        return 'status-lost';
      default:
        return 'status-available';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'available':
        return 'VerfÃ¼gbar';
      case 'booked':
        return 'Gebucht';
      case 'rented':
        return 'Verliehen';
      case 'maintenance':
        return 'Wartung';
      case 'damaged':
        return 'BeschÃ¤digt';
      case 'lost':
        return 'Verloren';
      default:
        return 'VerfÃ¼gbar';
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'camera':
        return 'ğŸ“·';
      case 'lens':
        return 'ğŸ”';
      case 'lighting':
        return 'ğŸ’¡';
      case 'audio':
        return 'ğŸ¤';
      case 'tripod':
        return 'ğŸ“';
      case 'grip':
        return 'ğŸ”§';
      case 'monitor':
        return 'ğŸ“º';
      case 'computer':
        return 'ğŸ’»';
      case 'cable':
        return 'ğŸ”Œ';
      case 'accessory':
        return 'ğŸ’';
      default:
        return 'ğŸ“¦';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!equipment) {
    return (
      <div className="text-center py-12">
        <h2 className="text-xl font-semibold text-gray-900">Equipment nicht gefunden</h2>
        <Link to="/equipment" className="btn-primary mt-4">
          ZurÃ¼ck zur Equipment-Liste
        </Link>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div className="flex items-center space-x-4">
          <div className="h-12 w-12 bg-primary-100 rounded-lg flex items-center justify-center">
            <span className="text-2xl">{getCategoryIcon(equipment.category)}</span>
          </div>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">{equipment.name}</h1>
            <p className="text-gray-600">{equipment.brand} {equipment.model}</p>
          </div>
        </div>

        <div className="mt-4 sm:mt-0 flex space-x-2">
          {isStaff && (
            <>
              <Link
                to={`/equipment/${id}/edit`}
                className="btn-outline btn-sm"
              >
                <PencilIcon className="h-4 w-4 mr-2" />
                Bearbeiten
              </Link>
              <button
                onClick={handleDelete}
                className="btn-danger btn-sm"
              >
                <TrashIcon className="h-4 w-4 mr-2" />
                LÃ¶schen
              </button>
            </>
          )}
          {equipment.status === 'available' && (
            <Link
              to={`/bookings/new?equipment=${id}`}
              className="btn-success btn-sm"
            >
              <CalendarIcon className="h-4 w-4 mr-2" />
              Buchen
            </Link>
          )}
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-6">
          {/* Images */}
          {equipment.images && equipment.images.length > 0 && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">Bilder</h2>
              </div>
              <div className="card-body">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  {equipment.images.map((image, index) => (
                    <div key={index} className="relative">
                      <img
                        src={image.url}
                        alt={image.alt || equipment.name}
                        className="w-full h-48 object-cover rounded-lg"
                      />
                      {image.isPrimary && (
                        <span className="absolute top-2 right-2 bg-primary-600 text-white text-xs px-2 py-1 rounded">
                          PrimÃ¤r
                        </span>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Description */}
          {equipment.description && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">Beschreibung</h2>
              </div>
              <div className="card-body">
                <p className="text-gray-700">{equipment.description}</p>
              </div>
            </div>
          )}

          {/* Specifications */}
          {equipment.specifications && Object.keys(equipment.specifications).length > 0 && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">Spezifikationen</h2>
              </div>
              <div className="card-body">
                <dl className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  {Object.entries(equipment.specifications).map(([key, value]) => (
                    <div key={key}>
                      <dt className="text-sm font-medium text-gray-500 capitalize">{key}</dt>
                      <dd className="text-sm text-gray-900">{value}</dd>
                    </div>
                  ))}
                </dl>
              </div>
            </div>
          )}

          {/* Maintenance History */}
          {equipment.maintenanceHistory && equipment.maintenanceHistory.length > 0 && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">Wartungshistorie</h2>
              </div>
              <div className="card-body">
                <div className="space-y-4">
                  {equipment.maintenanceHistory.map((maintenance, index) => (
                    <div key={index} className="border-l-4 border-primary-500 pl-4">
                      <div className="flex justify-between items-start">
                        <div>
                          <p className="text-sm font-medium text-gray-900">{maintenance.description}</p>
                          <p className="text-xs text-gray-500">
                            {new Date(maintenance.date).toLocaleDateString('de-DE')}
                          </p>
                        </div>
                        {maintenance.cost > 0 && (
                          <span className="text-sm font-medium text-gray-900">
                            â‚¬{maintenance.cost}
                          </span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Status Card */}
          <div className="card">
            <div className="card-header">
              <h2 className="text-lg font-semibold text-gray-900">Status</h2>
            </div>
            <div className="card-body">
              <div className="flex items-center justify-between">
                <span className={getStatusColor(equipment.status)}>
                  {getStatusText(equipment.status)}
                </span>
                {isStaff && (
                  <select
                    className="form-input text-sm"
                    value={equipment.status}
                    onChange={async (e) => {
                      try {
                        await equipmentAPI.updateStatus(id, e.target.value);
                        setEquipment(prev => ({ ...prev, status: e.target.value }));
                        toast.success('Status erfolgreich aktualisiert');
                      } catch (error) {
                        toast.error('Fehler beim Aktualisieren des Status');
                      }
                    }}
                  >
                    <option value="available">VerfÃ¼gbar</option>
                    <option value="booked">Gebucht</option>
                    <option value="rented">Verliehen</option>
                    <option value="maintenance">Wartung</option>
                    <option value="damaged">BeschÃ¤digt</option>
                    <option value="lost">Verloren</option>
                  </select>
                )}
              </div>
            </div>
          </div>

          {/* Details Card */}
          <div className="card">
            <div className="card-header">
              <h2 className="text-lg font-semibold text-gray-900">Details</h2>
            </div>
            <div className="card-body space-y-4">
              <div>
                <dt className="text-sm font-medium text-gray-500">Seriennummer</dt>
                <dd className="text-sm text-gray-900 font-mono">{equipment.serialNumber}</dd>
              </div>

              <div>
                <dt className="text-sm font-medium text-gray-500">Kategorie</dt>
                <dd className="text-sm text-gray-900 capitalize">{equipment.category}</dd>
              </div>

              {equipment.location && (
                <div>
                  <dt className="text-sm font-medium text-gray-500">Standort</dt>
                  <dd className="text-sm text-gray-900">{equipment.location.name}</dd>
                </div>
              )}

              {equipment.purchaseDate && (
                <div>
                  <dt className="text-sm font-medium text-gray-500">Kaufdatum</dt>
                  <dd className="text-sm text-gray-900">
                    {new Date(equipment.purchaseDate).toLocaleDateString('de-DE')}
                  </dd>
                </div>
              )}

              {equipment.currentValue && (
                <div>
                  <dt className="text-sm font-medium text-gray-500">Aktueller Wert</dt>
                  <dd className="text-sm text-gray-900">â‚¬{equipment.currentValue}</dd>
                </div>
              )}
            </div>
          </div>

          {/* Rental Rates */}
          {equipment.rentalRate && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">Mietpreise</h2>
              </div>
              <div className="card-body space-y-2">
                {equipment.rentalRate.daily > 0 && (
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Pro Tag</span>
                    <span className="text-sm font-medium">â‚¬{equipment.rentalRate.daily}</span>
                  </div>
                )}
                {equipment.rentalRate.weekly > 0 && (
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Pro Woche</span>
                    <span className="text-sm font-medium">â‚¬{equipment.rentalRate.weekly}</span>
                  </div>
                )}
                {equipment.rentalRate.monthly > 0 && (
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Pro Monat</span>
                    <span className="text-sm font-medium">â‚¬{equipment.rentalRate.monthly}</span>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Tags */}
          {equipment.tags && equipment.tags.length > 0 && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">Tags</h2>
              </div>
              <div className="card-body">
                <div className="flex flex-wrap gap-2">
                  {equipment.tags.map((tag, index) => (
                    <span
                      key={index}
                      className="inline-block bg-gray-100 text-gray-600 text-xs px-2 py-1 rounded-full"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* QR Code */}
          {isStaff && (
            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-gray-900">QR-Code</h2>
              </div>
              <div className="card-body">
                <a
                  href={`/api/equipment/${id}/qr`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn-outline btn-sm w-full"
                >
                  <QrCodeIcon className="h-4 w-4 mr-2" />
                  QR-Code herunterladen
                </a>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EquipmentDetail;
