import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { QrCodeIcon, CameraIcon, ArrowLeftIcon } from '@heroicons/react/24/outline';
import { equipmentAPI } from '../../services/api';
import toast from 'react-hot-toast';

const QRScanner = () => {
  const [scanning, setScanning] = useState(false);
  const [scannedData, setScannedData] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleScan = async (data) => {
    if (data) {
      setScanning(false);
      setLoading(true);

      try {
        // Parse QR code data
        const qrData = JSON.parse(data);

        if (qrData.type === 'equipment') {
          // Fetch equipment details
          const response = await equipmentAPI.getById(qrData.id);
          setScannedData(response.data.equipment);
          toast.success('Equipment gefunden!');
        } else {
          toast.error('UngÃ¼ltiger QR-Code');
        }
      } catch (error) {
        console.error('Error processing QR code:', error);
        toast.error('Fehler beim Verarbeiten des QR-Codes');
      } finally {
        setLoading(false);
      }
    }
  };

  const handleError = (err) => {
    console.error('QR Scanner error:', err);
    toast.error('Fehler beim Scannen des QR-Codes');
  };

  const startScanning = () => {
    setScanning(true);
    setScannedData(null);
  };

  const stopScanning = () => {
    setScanning(false);
  };

  const handleEquipmentAction = (action) => {
    if (scannedData) {
      switch (action) {
        case 'view':
          navigate(`/equipment/${scannedData._id}`);
          break;
        case 'book':
          navigate(`/bookings/new?equipment=${scannedData._id}`);
          break;
        case 'checkout':
          // Implement checkout logic
          toast.info('Checkout-Funktion wird implementiert');
          break;
        case 'checkin':
          // Implement checkin logic
          toast.info('Checkin-Funktion wird implementiert');
          break;
        default:
          break;
      }
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
