import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { CalendarIcon, ClockIcon, UserIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import { bookingService } from '../../services/bookingService';
import { equipmentService } from '../../services/equipmentService';
import { userService } from '../../services/userService';
import toast from 'react-hot-toast';

const BookingForm = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { id } = useParams(); // For editing existing booking
  const [loading, setLoading] = useState(false);
  const [equipment, setEquipment] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedEquipment, setSelectedEquipment] = useState(null);
  const [availability, setAvailability] = useState(null);

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors }
  } = useForm();

  const watchedStartDate = watch('startDate');
  const watchedEndDate = watch('endDate');
  const watchedEquipment = watch('equipment');

  useEffect(() => {
    loadEquipment();
    if (user.role === 'admin') {
      loadUsers();
    }
    if (id) {
      loadBooking();
    }
  }, [id, user.role]);

  useEffect(() => {
    if (watchedEquipment) {
      loadEquipmentDetails(watchedEquipment);
    }
  }, [watchedEquipment]);

  useEffect(() => {
    if (watchedStartDate && watchedEndDate && watchedEquipment) {
      checkAvailability();
    }
  }, [watchedStartDate, watchedEndDate, watchedEquipment]);

  const loadEquipment = async () => {
    try {
      const data = await equipmentService.getEquipment({ status: 'available' });
      setEquipment(data);
    } catch (error) {
      console.error('Error loading equipment:', error);
      toast.error('Fehler beim Laden der Geräte');
    }
  };

  const loadUsers = async () => {
    try {
      const data = await userService.getUsers();
      setUsers(data);
    } catch (error) {
      console.error('Error loading users:', error);
    }
  };

  const loadBooking = async () => {
    try {
      setLoading(true);
      const data = await bookingService.getBooking(id);
      Object.keys(data).forEach(key => {
        setValue(key, data[key]);
      });
      setSelectedEquipment(data.equipment);
    } catch (error) {
      console.error('Error loading booking:', error);
      toast.error('Fehler beim Laden der Buchung');
    } finally {
      setLoading(false);
    }
  };

  const loadEquipmentDetails = async (equipmentId) => {
    try {
      const data = await equipmentService.getEquipmentById(equipmentId);
      setSelectedEquipment(data);
    } catch (error) {
      console.error('Error loading equipment details:', error);
    }
  };

  const checkAvailability = async () => {
    if (!watchedStartDate || !watchedEndDate || !watchedEquipment) return;

    try {
      const data = await bookingService.checkAvailability({
        equipment: watchedEquipment,
        startDate: watchedStartDate,
        endDate: watchedEndDate
      });
      setAvailability(data);
    } catch (error) {
      console.error('Error checking availability:', error);
    }
  };

  const onSubmit = async (data) => {
    try {
      setLoading(true);

      if (id) {
        await bookingService.updateBooking(id, data);
        toast.success('Buchung erfolgreich aktualisiert');
      } else {
        await bookingService.createBooking(data);
        toast.success('Buchung erfolgreich erstellt');
      }

      navigate('/bookings');
    } catch (error) {
      console.error('Error saving booking:', error);
      toast.error('Fehler beim Speichern der Buchung');
    } finally {
      setLoading(false);
    }
  };

  const calculateDuration = () => {
    if (!watchedStartDate || !watchedEndDate) return 0;
    const start = new Date(watchedStartDate);
    const end = new Date(watchedEndDate);
    const diffTime = Math.abs(end - start);
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  if (loading && id) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900">
          {id ? 'Buchung bearbeiten' : 'Neue Buchung'}
        </h1>
        <p className="text-gray-600">
          {id ? 'Bearbeiten Sie die Buchungsdetails' : 'Erstellen Sie eine neue Equipment-Buchung'}
        </p>
      </div>

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        {/* Equipment Selection */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Equipment *
          </label>
          <select
            {...register('equipment', { required: 'Equipment ist erforderlich' })}
            className="form-select"
            disabled={!!id}
          >
            <option value="">Equipment auswählen</option>
            {equipment.map(item => (
              <option key={item._id} value={item._id}>
                {item.name} - {item.brand} {item.model}
              </option>
            ))}
          </select>
          {errors.equipment && (
            <p className="mt-1 text-sm text-red-600">{errors.equipment.message}</p>
          )}
        </div>

        {/* Selected Equipment Details */}
        {selectedEquipment && (
          <div className="bg-gray-50 p-4 rounded-lg">
            <h3 className="font-medium text-gray-900 mb-2">Ausgewähltes Equipment</h3>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-600">Name:</span>
                <p className="font-medium">{selectedEquipment.name}</p>
              </div>
              <div>
                <span className="text-gray-600">Kategorie:</span>
                <p className="font-medium">{selectedEquipment.category}</p>
              </div>
              <div>
                <span className="text-gray-600">Standort:</span>
                <p className="font-medium">{selectedEquipment.location?.name}</p>
              </div>
              <div>
                <span className="text-gray-600">Status:</span>
                <p className="font-medium">{selectedEquipment.status}</p>
              </div>
            </div>
          </div>
        )}

        {/* Date Range */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Startdatum *
            </label>
            <div className="relative">
              <input
                type="date"
                {...register('startDate', { required: 'Startdatum ist erforderlich' })}
                className="form-input pl-10"
                min={new Date().toISOString().split('T')[0]}
              />
              <CalendarIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            </div>
            {errors.startDate && (
              <p className="mt-1 text-sm text-red-600">{errors.startDate.message}</p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Enddatum *
            </label>
            <div className="relative">
              <input
                type="date"
                {...register('endDate', { required: 'Enddatum ist erforderlich' })}
                className="form-input pl-10"
                min={watchedStartDate || new Date().toISOString().split('T')[0]}
              />
              <CalendarIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            </div>
            {errors.endDate && (
              <p className="mt-1 text-sm text-red-600">{errors.endDate.message}</p>
            )}
          </div>
        </div>

        {/* Duration and Availability */}
        {watchedStartDate && watchedEndDate && (
          <div className="bg-blue-50 p-4 rounded-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <ClockIcon className="h-5 w-5 text-blue-600 mr-2" />
                <span className="text-sm font-medium text-blue-900">
                  Dauer: {calculateDuration()} Tag{calculateDuration() !== 1 ? 'e' : ''}
                </span>
              </div>
              {availability && (
                <span className={`text-sm font-medium ${
                  availability.available ? 'text-green-600' : 'text-red-600'
                }`}>
                  {availability.available ? 'Verfügbar' : 'Nicht verfügbar'}
                </span>
              )}
            </div>
            {availability && !availability.available && (
              <p className="mt-2 text-sm text-red-600">
                Das Equipment ist in diesem Zeitraum bereits gebucht.
              </p>
            )}
          </div>
        )}

        {/* User Selection (Admin only) */}
        {user.role === 'admin' && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Benutzer *
            </label>
            <div className="relative">
              <select
                {...register('user', { required: 'Benutzer ist erforderlich' })}
                className="form-select pl-10"
              >
                <option value="">Benutzer auswählen</option>
                {users.map(userItem => (
                  <option key={userItem._id} value={userItem._id}>
                    {userItem.firstName} {userItem.lastName} ({userItem.email})
                  </option>
                ))}
              </select>
              <UserIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            </div>
            {errors.user && (
              <p className="mt-1 text-sm text-red-600">{errors.user.message}</p>
            )}
          </div>
        )}

        {/* Notes */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Notizen
          </label>
          <textarea
            {...register('notes')}
            rows={3}
            className="form-textarea"
            placeholder="Zusätzliche Informationen zur Buchung..."
          />
        </div>

        {/* Submit Buttons */}
        <div className="flex justify-end gap-3">
          <button
            type="button"
            onClick={() => navigate('/bookings')}
            className="btn btn-secondary"
          >
            Abbrechen
          </button>
          <button
            type="submit"
            disabled={loading || (availability && !availability.available)}
            className="btn btn-primary"
          >
            {loading ? (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
            ) : (
              id ? 'Aktualisieren' : 'Buchung erstellen'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default BookingForm;
