import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { PlusIcon, MagnifyingGlassIcon, FunnelIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import { equipmentAPI } from '../../services/api';
import EquipmentCard from '../../components/Equipment/EquipmentCard';
import toast from 'react-hot-toast';

const EquipmentList = () => {
  const { isStaff } = useAuth();
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    category: '',
    status: '',
    location: '',
  });
  const [pagination, setPagination] = useState({
    current: 1,
    pages: 1,
    total: 0,
    limit: 12,
  });

  useEffect(() => {
    fetchEquipment();
  }, [filters, pagination.current]);

  const fetchEquipment = async () => {
    try {
      setLoading(true);
      const params = {
        page: pagination.current,
        limit: pagination.limit,
        ...filters,
      };

      const response = await equipmentAPI.getAll(params);
      setEquipment(response.data.equipment);
      setPagination(prev => ({
        ...prev,
        ...response.data.pagination,
      }));
    } catch (error) {
      console.error('Error fetching equipment:', error);
      toast.error('Fehler beim Laden der Equipment-Liste');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    setPagination(prev => ({ ...prev, current: 1 }));
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchEquipment();
  };

  const categories = [
    { value: '', label: 'Alle Kategorien' },
    { value: 'camera', label: 'Kamera' },
    { value: 'lens', label: 'Objektiv' },
    { value: 'lighting', label: 'Beleuchtung' },
    { value: 'audio', label: 'Audio' },
    { value: 'tripod', label: 'Stativ' },
    { value: 'grip', label: 'Grip' },
    { value: 'monitor', label: 'Monitor' },
    { value: 'computer', label: 'Computer' },
    { value: 'cable', label: 'Kabel' },
    { value: 'accessory', label: 'Zubehör' },
    { value: 'other', label: 'Sonstiges' },
  ];

  const statuses = [
    { value: '', label: 'Alle Status' },
    { value: 'available', label: 'Verfügbar' },
    { value: 'booked', label: 'Gebucht' },
    { value: 'rented', label: 'Verliehen' },
    { value: 'maintenance', label: 'Wartung' },
    { value: 'damaged', label: 'Beschädigt' },
    { value: 'lost', label: 'Verloren' },
  ];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Equipment</h1>
          <p className="mt-1 text-sm text-gray-600">
            Verwalten Sie Ihr Filmequipment und überwachen Sie den Status.
          </p>
        </div>
        {isStaff && (
          <div className="mt-4 sm:mt-0">
            <Link
              to="/equipment/new"
              className="btn-primary"
            >
              <PlusIcon className="h-4 w-4 mr-2" />
              Equipment hinzufügen
            </Link>
          </div>
        )}
      </div>

      {/* Filters */}
      <div className="card">
        <div className="card-body">
          <form onSubmit={handleSearch} className="space-y-4">
            {/* Search */}
            <div className="relative">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                placeholder="Equipment suchen..."
                className="form-input pl-10"
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
              />
            </div>

            {/* Filter Row */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="form-label">Kategorie</label>
                <select
                  className="form-input"
                  value={filters.category}
                  onChange={(e) => handleFilterChange('category', e.target.value)}
                >
                  {categories.map((category) => (
                    <option key={category.value} value={category.value}>
                      {category.label}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="form-label">Status</label>
                <select
                  className="form-input"
                  value={filters.status}
                  onChange={(e) => handleFilterChange('status', e.target.value)}
                >
                  {statuses.map((status) => (
                    <option key={status.value} value={status.value}>
                      {status.label}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="form-label">Standort</label>
                <input
                  type="text"
                  placeholder="Standort suchen..."
                  className="form-input"
                  value={filters.location}
                  onChange={(e) => handleFilterChange('location', e.target.value)}
                />
              </div>
            </div>

            {/* Filter Actions */}
            <div className="flex items-center justify-between">
              <button
                type="submit"
                className="btn-primary"
              >
                <FunnelIcon className="h-4 w-4 mr-2" />
                Filter anwenden
              </button>
              <button
                type="button"
                onClick={() => {
                  setFilters({
                    search: '',
                    category: '',
                    status: '',
                    location: '',
                  });
                  setPagination(prev => ({ ...prev, current: 1 }));
                }}
                className="text-sm text-gray-600 hover:text-gray-900"
              >
                Filter zurücksetzen
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Results */}
      <div className="flex items-center justify-between">
        <p className="text-sm text-gray-700">
          {pagination.total} Equipment gefunden
        </p>
        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-700">Anzeige:</span>
          <select
            className="form-input text-sm"
            value={pagination.limit}
            onChange={(e) => {
              setPagination(prev => ({
                ...prev,
                limit: parseInt(e.target.value),
                current: 1,
              }));
            }}
          >
            <option value={12}>12 pro Seite</option>
            <option value={24}>24 pro Seite</option>
            <option value={48}>48 pro Seite</option>
          </select>
        </div>
      </div>

      {/* Equipment Grid */}
      {loading ? (
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        </div>
      ) : equipment.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {equipment.map((item) => (
            <EquipmentCard key={item._id} equipment={item} />
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <div className="mx-auto h-12 w-12 text-gray-400">
            <svg fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9.172 16.172a4 4 0 015.656 0M9 12h6m-6-4h6m2 5.291A7.962 7.962 0 0112 15c-2.34 0-4.47-.881-6.08-2.33"
              />
            </svg>
          </div>
          <h3 className="mt-2 text-sm font-medium text-gray-900">Kein Equipment gefunden</h3>
          <p className="mt-1 text-sm text-gray-500">
            Versuchen Sie es mit anderen Suchkriterien.
          </p>
        </div>
      )}

      {/* Pagination */}
      {pagination.pages > 1 && (
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setPagination(prev => ({ ...prev, current: prev.current - 1 }))}
              disabled={pagination.current === 1}
              className="btn-outline btn-sm disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Zurück
            </button>
            <span className="text-sm text-gray-700">
              Seite {pagination.current} von {pagination.pages}
            </span>
            <button
              onClick={() => setPagination(prev => ({ ...prev, current: prev.current + 1 }))}
              disabled={pagination.current === pagination.pages}
              className="btn-outline btn-sm disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Weiter
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default EquipmentList;
